package aula20190408.grandesNumeros;

import java.math.BigDecimal;

public class App {
	
	public static void main(String[] args) {
		float valorFloat = 999_999_999_999_999.;
		double valorDouble = 99_999_999_999_999_999_999_999.;
		
		BigDecimal grandão = new BigDecimal("9999999999999999999999999999999999999999999999999999999999.");
		BigDecimal grandãoComDuasCasas = grandão.setScale(2, BigDecimal.ROUND_UP);
		
		System.out.println(valorFloat);
		System.out.println(valorDouble);
		System.out.println(grandão);
		System.out.println(grandãoComDuasCasas);
	}

}
